import React, { useRef, useEffect } from 'react'
import Projectcomponent from './Projectcomponent'
import { motion, useAnimation, useInView } from 'framer-motion'

const Projects = () => {

    const ref = useRef(null)
    const isInView = useInView(ref.current)

    const mainControls = useAnimation()

    useEffect(() => {
        if (isInView) {
            mainControls.start("visible")
            return
        }
    }, [isInView, mainControls])

    const projectData = [{
        name: "Oncode",
        color: "#fb6976",
        description: "Oncode is a online web based coding utility cum community that enables you to store, organize and üßë‚Äçüíª compile your codes at one place along with a sharable real time compiler üïê.Also a helpful community of your friends to solve your problems üòå."
    }, {
        name: "Newsfeed",
        color: "#1e3a8a",
        description: "Newsfeed is a API based application made with help of news api to fetch üì∞ news according to your notionality üáÆüá≥ and personal intersting categories ‚åò"
    }, {
        name: "Chatrooms",
        color: "#4ade80",
        description: "Chatrooms is full stack mern app üíö the allows you to create rooms of your personal interests using socket.io and connect with strangers üòå, initiate conversations and make friends ü§û"
    }]

    return (
        <motion.div className='mt-10 w-[65%] mx-auto text-center'
            ref={ref}
            variants={{
                hidden: { opacity: 0, y: 75 },
                visible: { opacity: 1, y: 0 }
            }}
            initial="hidden"
            animate={mainControls}
            transition={{ duration: 0.8, delay: 0.25 }}>
            <div className="head  text-6xl font-bold text-white">
                Projects ‚öíÔ∏è ‚Ü¥
                <div className="sub text-white text-base font-normal mt-5">
                    These are some of my major projects ‚Ü¶
                </div>
            </div>
            <div className="projects flex flex-wrap gap-4 justify-center mt-10">
                {projectData.map((p) => {
                    return (
                        <Projectcomponent name={p.name} color={p.color} description={p.description} />
                    )
                })}
            </div>


        </motion.div>
    )
}

export default Projects
