import React,{useRef,useEffect,useState} from 'react'
import Projectcomponent from './Projectcomponent'
import { motion, useAnimation, useInView } from 'framer-motion'

const Projects = () => {

    const ref = useRef(null)
    const isInView = useInView(ref,{once:false})

    const mainControls = useAnimation()

    useEffect(() => {
        if (isInView) {
            mainControls.start("visible")
            return
        }
    }, [isInView, mainControls])

    const projectData = [{
        name : "Oncode",
        color : "#fb6976",
        description : "Oncode is a online web based coding utility cum community that enables"
    },{
        name : "Newsfeed",
        color : "#1e3a8a",
        description : "Lorem ipsum dolor sit amet, consectetur adipisicing elit. Nemo ipsam, et repellat a neque obcaecati aliquam, itaque excepturi dolorum rerum, illum quibusdam sint."
    },{
        name : "Chatrooms",
        color : "#4ade80",
        description : "Lorem ipsum dolor sit amet, consectetur adipisicing elit. Nemo ipsam, et repellat a neque obcaecati aliquam, itaque excepturi dolorum rerum, illum quibusdam sint."
    }]

    return (
        <motion.div className='mt-10 w-[65%] mx-auto text-center'>
            <div className="head  text-6xl font-bold text-white">
                Projects ⚒️ ↴
                <div className="sub text-white text-base font-normal mt-5">
                    These are some of my major projects ↦
                </div>
            </div>
            <div className="projects flex flex-wrap gap-4 justify-center mt-10">
                    {projectData.map((p)=>{
                        return(
                            <Projectcomponent name={p.name} color={p.color} description={p.description}/>
                        )
                    })}
                </div>
        </motion.div>
    )
}

export default Projects
